    @props([        'itemsCount' => 0,        'totalPrice' => 0,        'pageName' => "",        'productsAmount' => 0,    ])  <style>    :root{      --gold:#cf9233;      --gold-dark:#b67a24;      --muted:#858585;      --card-bg:#ffffff;      --surface:#f6f6f6;      --radius:12px;    }    body{      background: linear-gradient(180deg,#f3f3f3 0%, #f8f8f8 100%);      color:#222;    }    /* container narrower like screenshot */    .page-wrap{      max-width:1100px;      margin: 0 auto;    }    /* left summary */    .summary-card{      background:var(--card-bg);      border-radius:var(--radius);      padding:22px;      box-shadow: 0 6px 20px rgba(12,12,30,0.06);      border:1px solid rgba(0,0,0,0.04);      position:sticky;      top: 105px;      animation: fadeUp .6s ease both;    }    .summary-card h5{      font-size: 20px;      font-weight: bold;      color:#222;      margin-bottom:18px;    }    .summary-row{      display:flex;      justify-content:space-between;      align-items:center;      margin:10px 0;      color:var(--muted);      font-size:14px;    }    .summary-total{      display:flex;      justify-content:space-between;      align-items:center;      margin-top:16px;      padding-top:12px;      border-top:1px dashed rgba(0,0,0,0.06);      font-weight:700;      color: var(--gold-dark);      font-size:16px;    }    .coupon-input{      display:flex;      gap:8px;      margin:18px 0;    }    .coupon-input .form-control{      border-radius:8px;    }    .apply-btn{      background:transparent;      color:var(--gold);      border:1px solid var(--gold);      border-radius:8px;      padding:6px 14px;      transition:all .18s ease;    }    .apply-btn:hover{ background:var(--gold); color:#fff; border-color:var(--gold-dark); }    .pay-btn{      width:100%;      background:linear-gradient(180deg,var(--gold) 0%, var(--gold-dark) 100%);      color:#fff;      border:none;      padding:12px;      border-radius:10px;      font-weight:700;      box-shadow: 0 6px 18px rgba(198,138,62,0.18);      transition:transform .18s ease;    }    .pay-btn:hover{ transform:translateY(-3px); }    /* right side */    .panel{      background:var(--card-bg);      border-radius:var(--radius);      padding:18px;      box-shadow: 0 6px 20px rgba(12,12,30,0.04);      border:1px solid rgba(0,0,0,0.04);      animation: fadeUp .6s ease both;    }    .panel h5{ font-weight:700; margin-bottom:14px; }    /* payment method card */    .method{      border-radius:10px;      padding:12px;      border:1px solid #eee;      transition:all .18s ease;      background: #fff;      margin-bottom:12px;      cursor:pointer;    }    .method:hover{ box-shadow:0 6px 18px rgba(0,0,0,0.04); border-color: rgba(207,146,51,0.2); transform:translateY(-3px); }    .method input[type="radio"]{ accent-color: var(--gold); transform:scale(1.05); margin-inline-start:6px }    .method img{ height:28px; }    .card-fields .form-control{border-radius:8px;background: #F8F8F8; }    .card-fields{width: 50% !important;}    /* small helper */    .muted{             color: var(--muted);        font-size: 13px;        font-weight: 300;    }    /* animation */    @keyframes fadeUp {      from{ opacity:0; transform: translateY(10px) }      to{ opacity:1; transform: translateY(0) }    }    /* responsive */    @media (max-width: 991px){      .page-wrap{ padding:0 16px; }      .summary-card{ position:static; margin-bottom:18px; }    }    .con-card{        display: flex;        padding-bottom: 7px;        border-bottom: 1px solid #D9D9D9;    }    .l-payment{        color: var(--muted);        font-size: 11.5px;        font-weight: 300;    }    .form-check-input:checked {        background-color: #CF9233;        border-color: #CF9233;    }  </style>  <form action="{{route('payment-chanal')}}" method="POST">    @csrf    <div class="page-wrap">        <div cla